// client/src/Controls.js
import React from "react";
import './Control.css';
function Controls({ cameraOn, micOn, toggleCamera, toggleMic }) {
  return (
    <div style={{ marginTop: 10 }}>
      <button onClick={toggleCamera}>
        {cameraOn ? "Turn Camera Off" : "Turn Camera On"}
      </button>
      <button onClick={toggleMic} style={{ marginLeft: 10 }}>
        {micOn ? "Mute" : "Unmute"}
      </button>
    </div>
  );
}

export default Controls;
