import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";

const socket = io("http://localhost:5000");

function App() {
  const [stream, setStream] = useState();
  const [myId, setMyId] = useState("");
  const [peers, setPeers] = useState([]);
  const myVideo = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    let currentStream;

    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((mediaStream) => {
      currentStream = mediaStream;
      setStream(mediaStream);
      if (myVideo.current) {
        myVideo.current.srcObject = mediaStream;
      }

      socket.on("connect", () => {
        setMyId(socket.id);
      });

      socket.on("user-joined", (userId) => {
        const peer = createPeer(userId, socket.id, currentStream);
        peersRef.current.push({ peerId: userId, peer });
        setPeers((prev) => [...prev, peer]);
      });

      socket.on("receive-signal", ({ signal, callerId }) => {
        const peer = addPeer(signal, callerId, currentStream);
        peersRef.current.push({ peerId: callerId, peer });
        setPeers((prev) => [...prev, peer]);
      });

      socket.on("receive-return-signal", ({ signal, callerId }) => {
        const item = peersRef.current.find(p => p.peerId === callerId);
        if (item) {
          item.peer.signal(signal);
        }
      });
    });

    return () => {
      if (currentStream) {
        currentStream.getTracks().forEach(track => track.stop());
      }

      peersRef.current.forEach(({ peer }) => peer.destroy());
      socket.disconnect();
    };
  }, []);

  function joinCall() {
    socket.emit("join-call", "test-room");
  }

  function createPeer(userToSignal, callerId, stream) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream
    });

    peer.on("signal", signal => {
      socket.emit("send-signal", { userToSignal, signal, callerId });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerId, stream) {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream
    });

    peer.on("signal", signal => {
      socket.emit("return-signal", { signal, callerId });
    });

    peer.signal(incomingSignal);

    return peer;
  }

  return (
    <div style={{ padding: 20 }}>
      <h2>Video Chat</h2>
      <button onClick={joinCall}>Join Call</button>
      <div style={{ display: "flex", gap: 10, marginTop: 20 }}>
        <video playsInline muted ref={myVideo} autoPlay style={{ width: "300px" }} />
        {peers.map((peer, index) => (
          <Video key={index} peer={peer} />
        ))}
      </div>
    </div>
  );
}

function Video({ peer }) {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });

    return () => {
      peer.removeAllListeners("stream");
    };
  }, [peer]);

  return <video playsInline autoPlay ref={ref} style={{ width: "300px" }} />;
}

export default App;
