import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import Control from "./Control"; // Import new file

const socket = io("http://localhost:5000");

function App() {
  const [stream, setStream] = useState();
  const [myId, setMyId] = useState("");
  const [peers, setPeers] = useState([]);
  const [cameraOn, setCameraOn] = useState(true);
  const [micOn, setMicOn] = useState(true);

  const myVideo = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    socket.on("connect", () => {
      setMyId(socket.id);
    });

    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((currentStream) => {
      setStream(currentStream);
      if (myVideo.current) {
        myVideo.current.srcObject = currentStream;
      }
    });

    socket.on("user-joined", (userId) => {
      if (!stream) return;
      const peer = createPeer(userId, socket.id, stream);
      peersRef.current.push({ peerId: userId, peer });
      setPeers((prev) => [...prev, peer]);
    });

    socket.on("receive-signal", ({ signal, callerId }) => {
      if (!stream) return;
      const peer = addPeer(signal, callerId, stream);
      peersRef.current.push({ peerId: callerId, peer });
      setPeers((prev) => [...prev, peer]);
    });

    socket.on("receive-return-signal", ({ signal, callerId }) => {
      const item = peersRef.current.find(p => p.peerId === callerId);
      if (item) {
        item.peer.signal(signal);
      }
    });

    return () => {
      socket.disconnect();
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, [stream]);

  function joinCall() {
    if (stream) {
      socket.emit("join-call", "test-room");
    } else {
      alert("Camera & mic not ready");
    }
  }

  function createPeer(userToSignal, callerId, stream) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream
    });

    peer.on("signal", signal => {
      socket.emit("send-signal", { userToSignal, signal, callerId });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerId, stream) {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream
    });

    peer.on("signal", signal => {
      socket.emit("return-signal", { signal, callerId });
    });

    peer.signal(incomingSignal);

    return peer;
  }

  function toggleCamera() {
    const videoTrack = stream?.getVideoTracks()[0];
    if (videoTrack) {
      videoTrack.enabled = !videoTrack.enabled;
      setCameraOn(videoTrack.enabled);
    }
  }

  function toggleMic() {
    const audioTrack = stream?.getAudioTracks()[0];
    if (audioTrack) {
      audioTrack.enabled = !audioTrack.enabled;
      setMicOn(audioTrack.enabled);
    }
  }

  return (
    <div style={{ padding: 20, backgroundColor: "#F3E8FF", minHeight: "100vh" }}>
      <h2 style={{ color: "#6A1B9A" }}>Video Chat</h2>
      <button onClick={joinCall} style={{ background: "#C084FC", color: "white", padding: "8px 16px", border: "none", borderRadius: "6px" }}>
        Join Call
      </button>
      <Controls cameraOn={cameraOn} micOn={micOn} toggleCamera={toggleCamera} toggleMic={toggleMic} />
      <div style={{ display: "flex", flexWrap: "wrap", gap: 10, marginTop: 20 }}>
        <video playsInline muted ref={myVideo} autoPlay style={{ width: "300px", borderRadius: 10 }} />
        {peers.map((peer, index) => (
          <Video key={index} peer={peer} />
        ))}
      </div>
    </div>
  );
}

function Video({ peer }) {
  const ref = useRef();

  useEffect(() => {
    if (!peer?.on) return;

    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });

    return () => {
      if (peer?.removeAllListeners) {
        peer.removeAllListeners("stream");
      }
    };
  }, [peer]);

  return <video playsInline autoPlay ref={ref} style={{ width: "300px", borderRadius: 10 }} />;
}

export default App;
