import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import './App.css';
import Control from "./Control";


const socket = io("http://192.168.0.189:5000");

function App() {
  const [stream, setStream] = useState(null);
  const [peers, setPeers] = useState([]);
  const myVideo = useRef();
  const peersRef = useRef([]);
  const [cameraOn, setCameraOn] = useState(true); 
  const [micOn, setMicOn] = useState(true);

  useEffect(() => {
    // Get media
    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then(mediaStream => {
      setStream(mediaStream);
      if (myVideo.current) {
        myVideo.current.srcObject = mediaStream;
      }

      // Connect to socket
      socket.on("connect", () => {
        console.log("Socket connected:", socket.id);
      });

      // Another user joined after us
      socket.on("user-joined", (userId) => {
        if (!stream) return;

        const peer = createPeer(userId, socket.id, stream);
        if (peer) {
            peersRef.current.push({ peerId: userId, peer });
            setPeers((prev) => [...prev, peer]);
        }
        });


      // Another user sent us their signal
      socket.on("receive-signal", ({ signal, callerId }) => {
        if (!stream) return;

        const peer = addPeer(signal, callerId, stream);
        if (peer) {
            peersRef.current.push({ peerId: callerId, peer });
            setPeers((prev) => [...prev, peer]);
        }
        });


      socket.on("receive-return-signal", ({ signal, callerId }) => {
        const item = peersRef.current.find(p => p.peerId === callerId);
        if (item) {
          item.peer.signal(signal);
        }
      });
    });

    return () => {
      socket.disconnect();
      stream?.getTracks().forEach(track => track.stop());
      peersRef.current.forEach(({ peer }) => peer.destroy());
    };
  }, []);

  function joinCall() {
    socket.emit("join-call", "test-room");
  }

  function createPeer(userToSignal, callerId, stream) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", signal => {
      socket.emit("send-signal", { userToSignal, signal, callerId });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerId, stream) {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", signal => {
      socket.emit("return-signal", { signal, callerId });
    });

    peer.signal(incomingSignal);
    return peer;
  }

        return (
        <div className="container">
            <h2>Video Chat</h2>
            <div className="videos">
            <video playsInline muted ref={myVideo} autoPlay />
            {peers.map((peer, index) => (
                <Video key={index} peer={peer} />
            ))}
            </div>
            <button onClick={joinCall}>Join Call</button>
            <Control
            stream={stream}
            cameraOn={cameraOn}
            micOn={micOn}
            setCameraOn={setCameraOn}
            setMicOn={setMicOn}
            />

        </div>
        );


}

function Video({ peer }) {
  const ref = useRef();

  useEffect(() => {
    if (!peer?.on) return; // prevents crash
    peer.on("stream", (stream) => {
        if (ref.current) {
        ref.current.srcObject = stream;
        }
    });

    return () => {
        if (peer?.removeAllListeners) {
        peer.removeAllListeners("stream");
        }
    };
    }, [peer]);

  return (
    <video
      playsInline
      autoPlay
      ref={ref}
      style={{ width: "300px", border: "1px solid blue" }}
    />
  );
}

export default App;
